package com.test.controller;import com.test.bean.DubboBean;import com.test.utils.DubboUtil;import io.swagger.annotations.Api;import io.swagger.annotations.ApiOperation;import lombok.extern.slf4j.Slf4j;import org.springframework.web.bind.annotation.*;@RestController@Slf4j@RequestMapping("/dubboClient")@Api(value = "DubboClient", description = "dubbo接口调用")public class DubboClientController {    /**     * dubbo接口调用     * @param dubboBean     * @return     */    @PostMapping(value = "/dubboTest",consumes = "application/json;charset=UTF-8")    @ResponseBody    @ApiOperation(value = "dubbo接口泛化调用", notes = "dubbo接口泛化调用")    public Object bubboTest(@RequestBody DubboBean dubboBean){        Object o = new Object();        if(dubboBean.getParams() == null || dubboBean.getParams() == "") {            o = DubboUtil.parsmsFalse(dubboBean.getApplicationName(),dubboBean.getZkAddr(),dubboBean.getInterfaceAddr()                    ,dubboBean.getRef(),dubboBean.getGroup(),dubboBean.getVersion());        } else {            String[] strs = dubboBean.getClassAddr().split(",");            o = DubboUtil.parsmsTure(dubboBean.getApplicationName(),dubboBean.getZkAddr(),dubboBean.getInterfaceAddr()                    ,dubboBean.getRef(),dubboBean.getGroup(),dubboBean.getVersion(),strs,dubboBean.getParams());        }        return o;    }}