package com.test.controller;import com.test.bean.McqBean;import com.test.utils.AjaxResponseBody;import com.test.utils.McqUtil;import io.swagger.annotations.Api;import io.swagger.annotations.ApiOperation;import lombok.extern.slf4j.Slf4j;import org.apache.http.HttpStatus;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;@RestController@Slf4j@RequestMapping("/mcqClient")@Api(value = "McqController", description = "Mcq调用")public class McqController {    @Autowired    McqUtil mcqUtil;    /**     * mcq调用     * @param m     * @return     */    @PostMapping(value = "/httpMcq",consumes = "application/json;charset=UTF-8")    @ResponseBody    @ApiOperation(value = "Mcq调用", notes = "Mcq调用")    public AjaxResponseBody bubboTest(@RequestBody McqBean m) {        String str = McqUtil.sendMsg(m.getMsg(),m.getProducerGroup(),m.getTopic(),m.getAddr(),m.getTags(),m.getKeys());        return new AjaxResponseBody(HttpStatus.SC_OK,"",str,"");    }}