package com.test.utils;import com.alibaba.rocketmq.client.producer.DefaultMQProducer;import com.alibaba.rocketmq.client.producer.SendResult;import com.alibaba.rocketmq.common.message.Message;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.stereotype.Component;@Componentpublic class McqUtil {    static Logger log = LoggerFactory.getLogger(McqUtil.class);    public SendResult sendMsg(String msg,String producerGroup,String topic,String addr,String tags,String keys) {        String msgId = "";        SendResult result=null;        DefaultMQProducer producer = new DefaultMQProducer(producerGroup);        producer.setNamesrvAddr(addr);        try {            producer.start();            Message message = new Message(topic, tags,keys,msg.getBytes("UTF-8"));            result = producer.send(message);            log.info("result{}",result);        } catch (Exception e) {            e.printStackTrace();        } finally {            producer.shutdown();        }        return result;    }}